class TransliterationDataLoader(Dataset):
    def __init__(self, filename):
        self.eng_words, self.hindi_words = self.readXmlDataset(filename, cleanHindiVocab)
        self.shuffle_indices = list(range(len(self.eng_words)))
        random.shuffle(self.shuffle_indices)
        self.shuffle_start_index = 0

    def __len__(self):
        return len(self.eng_words)

    def __getitem__(self, idx):
        return self.eng_words[idx], self.hindi_words[idx]

    def readXmlDataset(self, filename, cleanHindiVocab):
        transliterationCorpus = ET.parse(filename).getroot()
        lang1_words = []
        lang2_words = []

        for line in transliterationCorpus:
            wordlist1 = cleanEnglishVocab(line[0].text)
            wordlist2 = cleanHindiVocab(line[1].text)

            # Calculate alignment length
            alignment_length = self.get_alignment_length(line[0].text, line[1].text)

            # Handle misalignment by padding the shorter list with a placeholder symbol
            if len(wordlist1) > len(wordlist2):
                # Adding extra pad_char characters to the wordlist2 so that it becomes equals to the len(wordlist1)
                wordlist2 += [' '] * (len(wordlist1) - len(wordlist2))
            elif len(wordlist1) < len(wordlist2):
                wordlist1 += [' '] * (len(wordlist2) - len(wordlist1))

            for word in wordlist1:
                lang1_words.append(word)
            for word in wordlist2:
                lang2_words.append(word)

        return lang1_words, lang2_words

    def get_alignment_length(self, str1, str2):
        matcher = difflib.SequenceMatcher(None, str1, str2)
        alignment = matcher.get_opcodes()
        alignment_length = sum([op[4] - op[3] for op in alignment if op[0] == 'equal'])
        return alignment_length

    def get_random_sample(self):
        return self.__getitem__(random.randint(0, len(self.eng_words) - 1))

    def get_batch_from_array(self, batch_size, array):
        end = self.shuffle_start_index + batch_size
        batch = []
        if end >= len(self.eng_words):
            batch = [array[i] for i in self.shuffle_indices[0:end % len(self.eng_words)]]
            end = len(self.eng_words)
        return batch + [array[i] for i in self.shuffle_indices[self.shuffle_start_index : end]]

    def get_batch(self, batch_size, postprocess=True):
        eng_batch = self.get_batch_from_array(batch_size, self.eng_words)
        hindi_batch = self.get_batch_from_array(batch_size, self.hindi_words)
        self.shuffle_start_index += batch_size + 1

        # Reshuffle if 1 epoch is complete
        if self.shuffle_start_index >= len(self.eng_words):
            random.shuffle(self.shuffle_indices)
            self.shuffle_start_index = 0

        return eng_batch, hindi_batch

