"""# Training"""

def train_batch(net, opt, criterion, batch_size, device=device_gpu, teacher_force= True):
  # During the training phase, it's common to use teacher forcing, which means setting teacher_forcing=True. This helps stabilize the training process and allows the model to learn from the ground truth or actual target sequence.
  net.train().to(device)
  opt.zero_grad()
  eng_batch, hindi_batch = train_data.get_batch(batch_size)

  total_loss = 0
  for i in range(batch_size):

    input = word_rep(eng_batch[i], eng_alpha2index, device)
    # The ground truth is represented by the variable gt, which corresponds to the Hindi target sequence.
    gt = gt_rep(hindi_batch[i], hindi_alpha2index, device)
    outputs = net(input, gt.shape[0], device, ground_truth=gt if teacher_force else None)

    for index, output in enumerate(outputs):
      # The loss between the model's output and the ground truth at each time step.
      loss = criterion(output, gt[index])/ batch_size
      loss.backward(retain_graph = True)
      total_loss += loss
  opt.step()
  return total_loss/batch_size

def train_setup(net, lr=0.001, n_epochs=10, batch_size=100, momentum=0.9, display_freq=10, device=device_gpu):
    net = net.to(device)
    criterion = nn.NLLLoss(ignore_index=-1)    # Negative Log Likelihood is simply a cross-entropy
    opt = optim.Adam(net.parameters(), lr=lr, betas=(momentum, 0.999))
    teacher_force_upto = n_epochs // 3   # teacher forcing will be used for the first third of the training epochs, and after that as the training progress, it will be set to False and thus the model will gradually transitioned to use its own predictions.

    total_samples = len(train_data)  # Assuming train_data is your dataset
    n_iterations_per_epoch = total_samples // batch_size
    print ("No. of iterations to be taken :", n_iterations_per_epoch, "for every single epochs")

    loss_arr = np.zeros(n_epochs * n_iterations_per_epoch + 1)

    for epoch in range(n_epochs):
        for iteration in range(n_iterations_per_epoch):
            current_iteration = epoch * n_iterations_per_epoch + iteration
            # teacher_force parameter is set based on whether the current iteration is less than teacher_force_upto. If True, teacher forcing is used; if False, the model uses its own predictions.
            loss_arr[current_iteration + 1] = (loss_arr[current_iteration] * current_iteration +
                                                train_batch(net, opt, criterion, batch_size, device=device,
                                                            teacher_force=current_iteration < teacher_force_upto)) / (
                                                   current_iteration + 1)

            if current_iteration % display_freq == display_freq - 1:
                clear_output(wait=True)

                print('Epoch', epoch, 'Iteration', current_iteration, 'Loss', loss_arr[current_iteration])
                plt.figure()
                plt.plot(loss_arr[1:current_iteration], '-*')
                plt.xlabel('Iteration')
                plt.ylabel('Loss')
                plt.show()  # For every 10 (= display_freq) iterations, it'll show the loss output
                print('\n\n')

    torch.save(net, 'model.pt')
    return loss_arr

"""# Encoder-Decoder (Using GRU)

*In opting for GRU (Gated Recurrent Unit) over LSTM (Long Short-Term Memory) in our machine transliteration model, we prioritize computational efficiency and streamlined architecture. GRUs offer faster training times and reduced parameter complexity, making them particularly advantageous when dealing with large datasets or aiming to control model complexity. The decision aligns with the need for efficient learning, especially in scenarios where training speed and resource utilization are key considerations.*
"""

MAX_OUTPUT_CHARS = 30 # Since, no. of characters in an English word might be more or less to a hindi word. So, to put certain restrictions on where the GRU should go we're defining Max. len. of output characters

# The model or a network we're using here is Encoder-Decoder
class Transliteration_EncoderDecoder(nn.Module):

    def __init__(self, input_size, hidden_size, extra_layer_size, output_size, verbose=False):
        super(Transliteration_EncoderDecoder, self).__init__()

        # Defining the size of the layers
        self.hidden_size = hidden_size
        self.output_size = output_size
        self.extra_layer_size = extra_layer_size

        # Created one encoder
        self.encoder_rnn_cell = nn.GRU(input_size, hidden_size)

        # Additional linear layer and non-linear activation function
        self.extra_layer = nn.Linear(hidden_size, extra_layer_size)
        self.relu = nn.ReLU()

        # Created one decoder
        self.decoder_rnn_cell = nn.GRU(output_size, hidden_size)
        self.h2o = nn.Linear(hidden_size, output_size) # hidden to output is the last layer where we're trying to predict which letter should be there. So, we're defining it as an fully connected layer of size of length hidden_size*output_size
        self.softmax = nn.LogSoftmax(dim=2) # applying softmax as an activation func. at last layer

        self.verbose = verbose # verbose is a kind of command that is going to keep a track of all the intermediate input layers of variable size


    def forward(self, input, max_output_chars = MAX_OUTPUT_CHARS, device = device_gpu, ground_truth = None):

        # Defining the encoder
        out, hidden = self.encoder_rnn_cell(input)

        if self.verbose:
            print('\n', 'Encoder input', input.shape)
            print('\n', 'Encoder output', out.shape)
            print('\n', 'Encoder hidden', hidden.shape)

        # Additional layer
        extra_layer_output = self.relu(self.extra_layer(hidden))

        if self.verbose:
            print('\n', 'Extra layer output', extra_layer_output.shape)

        # Defining the decoder
        decoder_state = extra_layer_output
        decoder_input = torch.zeros(1, 1, self.output_size).to(device)
        outputs = []

        if self.verbose:
            print('\n', 'Decoder state', decoder_state.shape)
            print('\n', 'Decoder input', decoder_input.shape)

        for i in range(max_output_chars):
            out, decoder_state = self.decoder_rnn_cell(decoder_input, decoder_state) # hidden state of decoder

            if self.verbose:
                print('\n', 'Decoder intermediate output', out.shape)

            out = self.h2o(decoder_state)
            out = self.softmax(out)
            outputs.append(out.view(1, -1))

            if self.verbose:
                print('\n', 'Decoder output', out.shape)
                self.verbose = False

            max_idx = torch.argmax(out, 2, keepdim=True)
            if not ground_truth is None:
                max_idx = ground_truth[i].reshape(1, 1, 1)
            one_hot = torch.FloatTensor(out.shape).to(device)
            one_hot.zero_()
            one_hot.scatter_(2, max_idx, 1)

            decoder_input = one_hot.detach() # (.detach) helps in not including gradients of this layer in back propagation training, i.e. while doing backpropagation it won't touch the decoded input

        return outputs

